.footer {
  background-color: hsl(233, 12%, 13%);

  @media (min-width: 768px) {
    grid-template-columns: repeat(4, 1fr);
  }
}

.footer-content {
  padding: 1rem;
}

.footer-logo {
  display: none;

  @media (min-width: 768px) {
    display: inline-flex;
    align-items: center;

    img {
      width: 160px;
      height: 30px;
    }
  }
}

.update-section {
  grid-column: 1 / 3;

  @media (min-width: 768px) {
    grid-column: 4 / 5;
    position: relative;
    top: 4rem;
  }

  .update-input {
    padding: .9rem;
    border-radius: 25px;
    border: 0;
    width: 70%;
    margin: 0 auto 4rem;

    @media (min-width: 768px) {
      margin: 0 auto;
    }
  }

  .update-btn {
    margin-left: .3rem;
    button {
      padding: .9rem 8rem;
    }
  }
}

.footer-links-one {
  padding: 0 1rem;
  margin: 0 auto;

  @media (min-width: 768px) {
    grid-column: 2 / 3;
    position: relative;
    left: -9rem;
  }

  ul {
    margin-bottom: 3rem;

    @media (min-width: 768px) {
      margin-bottom: 0;
    }

    li {
      list-style-type: none;
      margin-bottom: 1.2rem;

      a {
        color: white;
        text-decoration: none;
        transition: hover .2s ease-in-out;

        &:hover {
          color: hsl(12, 88%, 59%);
        }
      }
    }
  }

}

.footer-links-two {
  padding: 0 1rem;
  margin: 0 auto;

  @media screen and (min-width: 768px) {
    grid-column: 3 / 4;
    position: relative;
    right: 5rem;
  }
    

  ul {
    margin-bottom: 3rem;

    @media (min-width: 768px) {
      margin-bottom: 0;
    }

    li {
      list-style-type: none;
      margin-bottom: 1.2rem;

      a {
        color: white;
        text-decoration: none;
        transition: hover .2s ease-in-out;
        
        &:hover {
          color: hsl(12, 88%, 59%);
        }
      }
    }
  }

}

.footer-socials {
  padding: 0 1rem;
  grid-column: 1 / 3;

  @media (min-width: 768px) {
    grid-column: 1 / 2;
  }

  ul {
    margin-bottom: 2rem;

    @media (min-width: 768px) {
      margin-bottom: 0;
    }


    li {
      list-style-type: none;
      display: inline;
      margin-right: 1.2rem;

      a {
        color: white;
        text-decoration: none;

        img {
          width: 50px;

          @media screen and (min-width: 768px) {
            width: 30px;
          }
            
          }
        
      }
    }
  }

}

