sources.scss\\layout\\_features.scss:
.features {

  @media screen and (min-width: 768px) {
    margin-bottom: 2rem;
  }
}

.features-content {
  padding: 1rem;
  gap: 7rem;
}

.features-text {
  text-align: center;
  padding: 0 1rem;

  @media (min-width: 768px) {
    text-align: left;
    margin-bottom: 0;
  }

  .features__header {
    margin-bottom: 2rem;
    color: hsl(228, 39%, 23%);
  }

  p {
    line-height: 2rem;
    opacity: .5;
  }
}

.features-items {
  counter-reset: counter-name;
  margin-top: .5rem;

  .features-item {
    width: 100%;
    padding: .6rem 1.8rem;
    margin-bottom: .5rem;
    list-style-type: none;
    position: relative;
    left: 2.1rem;
    background-color: lighten($color: hsl(12, 88%, 59%), $amount: 35%);
    border-radius: 20px;

    @media (min-width: 768px) {
      background-color: white;
      padding: 0;
      margin-bottom: 1rem;
    }
  }

  .features-item::before {
    content: counter(counter-name);
    counter-increment: counter-name;
    display: inline;
    position: absolute;
    top: -0.10rem;
    left: -2.7rem;
    background-color: hsl(12, 88%, 59%);
    color: white;
    padding: .9em 1.8rem;
    border-radius: 25px;

    @media (min-width: 768px) {
      left: -4.7rem;
      top: -.5rem;
    }
  }

  h3 {
    color: hsl(228, 39%, 23%);
  }

  p {
    line-height: 2rem;
    opacity: .5;
    margin-bottom: 2rem;
  }
}

sources.scss\\layout\\_review.scss:
.review {

  margin-bottom: 2.5rem;

  @media screen and (min-width: 768px) {
    margin-bottom: 5rem;
  }
}



.review__content {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: .5rem;
}
.review__header {

  h2 {
    margin: 0; 
    color: hsl(228, 39%, 23%);
    text-align: center;
  }
 
}

.review__cards {
  gap: 1rem;
  margin: 4rem auto;
  

  .review__card {
    position: relative;
    padding: 0 2rem;
    border-radius: 10px;
    margin: 1rem auto;
    background-color: lighten($color: #999, $amount: 35%);


    .review__img {
      position: absolute;
      display: flex;
      top: -4.1rem;
      left: 10.5rem;
    }

    .review__text {
      margin: 2rem auto;
      line-height: 1.5;
      text-align: center;
  
      h3 {
        color: hsl(228, 39%, 23%);
      }
  
      p {
        opacity: .6;
      }
    }

    &:nth-child(1) {
      display: none;
  
      @media (min-width: 768px) {
        display: block;
      }
    }
  
    &:nth-child(3) {
      display: none;
      
      @media (min-width: 768px) {
        display: block;
      }
    }


  }
  
}

